import { IncomingMessage } from 'http'

declare module 'body-parsec' {
  export function raw(req: IncomingMessage, next?: Function): any
  export function form(req: IncomingMessage, next?: Function): any
  export function json(req: IncomingMessage, next?: Function): any
  export function text(req: IncomingMessage, next?: Function): any
  export function custom(
    req: IncomingMessage,
    fn: Function,
    next?: Function
  ): any

  export interface ReqWithBody extends IncomingMessage {
    body: string
  }
}
